import { Component } from '@angular/core';
import {User} from "../../user";
import {Comment, Image, Title} from "../../title";
import {HttpClient} from "@angular/common/http";
import {ActivatedRoute, Router} from "@angular/router";
import {environment} from "../../environment";

@Component({
  selector: 'app-profile-view',
  templateUrl: './profile-view.component.html',
  styleUrls: ['./profile-view.component.css', '../profile.component.css', '../../title-view/title-view.component.css']
})
export class ProfileViewComponent {
  user: User | undefined;
  commentedTitles: Title[] | undefined
  isSubscribed: boolean = false


  constructor(private httpclient: HttpClient, private router: Router, private route: ActivatedRoute) {
    let id = this.route.snapshot.paramMap.get('id');
    this.httpclient.get<User>(`${environment.serverUrl}/account/profile-view/${id}`).subscribe(user => {
      this.user = user
      this.checkSubscribe();
    });
  }

  parseDate(date: Date): Date {
    return new Date(date);
  }

  getCreatingDate(date: Date): string {
    date = this.parseDate(date);
    return date.toLocaleTimeString() +" "+ date.toLocaleDateString()
  }


  getLimitComments() {
    let id = this.route.snapshot.paramMap.get('id');
    this.httpclient.get<Title[]>(`${environment.serverUrl}/account/commented/${id}`).subscribe(titles => {
      this.commentedTitles = titles;
    });
  }

  subscribe() {
    let id = this.route.snapshot.paramMap.get('id');
    this.httpclient.post<number>(`${environment.serverUrl}/account/subscribe/${id}`, {}).subscribe(() => {
      document.location.reload();
    });
  }

  unSubscribe() {
    let id = this.route.snapshot.paramMap.get('id');
    this.httpclient.post<number>(`${environment.serverUrl}/account/unsubscribe/${id}`, {}).subscribe(() => {
      document.location.reload();
    });
  }


  checkSubscribe() {
    if (this.user) {
      let id = this.user.id
      this.httpclient.get<boolean>(`${environment.serverUrl}/account/check-sub/${id}`).subscribe(isSubscribed => {
        this.isSubscribed = isSubscribed;
      });
    }
  }

  getReleaseYear(u: User): string {
    return new Date(u.creationDate).toLocaleDateString();
  }

  ngOnInit(): void {
    this.getLimitComments();
  }

}
<div *ngIf="user" class="justify-content-center">
  <div class="col-md-9 col-lg-9 mx-auto">
    <img [src]="user.img64" alt="Admin" class="w-100 bg-wgr">

    <div *ngIf="user.role == 'AUTHOR' && !isSubscribed" class="col-sm-12">
      <button (click)="subscribe()" class="w-100 bg-warning">Ви не підписані на автора
        <br>
        <small>Оформити підписку?</small>
      </button>
    </div>

    <div *ngIf="user.role == 'AUTHOR' && isSubscribed" class="col-sm-12">
      <button (click)="unSubscribe()" class="w-100 bg-gr">
        <strong>Ви вже підписані</strong>
        <br>
        <small>Відключити підписку?</small>
      </button>
    </div>

    <div class="row g-0 p-2 bg-wgr">

      <div class="col-sm-6">
        <strong class="title" style="color: black;">Ім'я</strong>
        <input type="text" class="form-control p-1" id="firstName"
               placeholder="{{user.firstName}}" disabled value="" required="">
      </div>

      <div class="col-sm-6">
        <strong class="title" style="color: black;">Прізвище</strong>
        <input type="text" class="form-control p-1" id="lastName" placeholder="{{user.lastName}}"
               disabled value="" required="">
      </div>

      <div class="col-sm-6">
        <strong class="title" style="color: black;">Нікнейм</strong>
        <div class="input-group has-validation">
          <input type="text" class="form-control p-1" id="username"
                 placeholder="{{user.nickName}}" disabled>
        </div>
      </div>

      <div class="col-sm-6">
        <strong class="title" style="color: black;">Роль</strong>
        <input type="text" class="form-control p-1" id="role" placeholder="{{user.role}}"
               disabled value="">
      </div>


      <div class="col-sm-12">
        <strong class="title" style="color: black;">Email
        </strong>
        <input type="email" class="form-control p-1" id="email" placeholder="{{user.email}}"
               disabled>
      </div>

      <div class="col-sm-12">
        <strong class="title" style="color: black;">Дата реєстрації
        </strong>
        <input type="text" class="form-control p-1" id="date" placeholder="{{parseDate(user.creationDate)}}" disabled>
      </div>

    </div>
  </div>


  <div class="col-md-9 col-lg-9 mx-auto" style="margin-top: 20px;">
    <div *ngIf="!(user.publishedTitles && user.publishedTitles.length > 0)">
      <a class="btn-lg w-100 bg-danger not-border-bottom">Користувач нічого не опублікував</a>
    </div>
    <div *ngIf=" user.publishedTitles && user.publishedTitles.length > 0">
      <a class="btn-lg w-100 not-border-bottom">Публікаці створені користувачем</a>
      <div class="col-12" *ngFor="let title of user.publishedTitles">
        <div
          class="row g-0  border rounded not-border-top overflow-hidden flex-md-row pb-1 mb-1 shadow-sm h-md-250 position-relative">
          <div class="col p-2 likes-inner d-flex flex-column position-static">
            <h4 class="mb-0">{{title.titleName}}</h4>
            <div class="d-flex">
              <span>оригінальний автор: </span>
              <strong class="text-muted">{{title.originalAuthor}}</strong>
            </div>
            <div class="d-flex">
              <span>дата створення: </span>
              <div class="mb-1 text-muted" *ngIf="title.creationDate">{{parseDate(title.creationDate)}}</div>
              <div class="mb-1 text-muted" *ngIf="!title.creationDate">[date parse error]</div>
            </div>
            <textarea style="border: 0" class="w-100" disabled>{{title.description}}</textarea>
            <p class="card-text mb-auto">

            </p>
          </div>
          <div class="col-auto d-none d-lg-block">
            <img [src]="title.titleImg" alt="" class="bd-placeholder-img" width="100">
          </div>
        </div>
      </div>
    </div>

  </div>
</div>
