import {Component, OnInit} from '@angular/core';
import {environment} from "../../environment";
import {User} from "../../user";
import {AppComponent} from "../../app.component";
import {HttpClient} from "@angular/common/http";
import {Router} from "@angular/router";
import {Image} from "../../title";

@Component({
  selector: 'app-prof-edit',
  templateUrl: './prof-edit.component.html',
  styleUrls: ['../auth-page.component.css', '../../publish/publish.component.css', '../../title-view/title-view.component.css']
})
export class ProfEditComponent  {
  name: string | undefined
  firstName: string | undefined
  lastName: string | undefined
  img: any = ''

  userExist: boolean = false
  emptyName: boolean = false

  user: User | undefined = AppComponent.getUser();
  constructor(private httpclient: HttpClient, private router: Router) {
  }

  validation() {
    if (!this.name) {
      this.emptyName = true;
      return;
    }

    this.emptyName = false;
    this.userExist = false;
    this.changeProfileInfo();
  }

  changeProfileInfo() {
    this.httpclient.post<User>(`${environment.serverUrl}/account/edit`, {
      nickName: this.name,
      firstName: this.firstName,
      lastName: this.lastName,
    }).subscribe(() => {this.router.navigate(['/profile'])});
  }
}
<section class="text-center" *ngIf="user" >
  <div class="card mx-4 mx-md-5 shadow-5-strong" style="
        background: hsla(0, 0%, 100%, 0.8);
        backdrop-filter: blur(30px);
        border: 0;
        ">

    <style>
      .custom-image-container-main label {
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 150%;
        cursor: pointer;
        width: 100%;
        height: 620px;
        border: solid 1px black;
        border-radius: 5px;
        object-fit: cover;
      }
    </style>

    <div class="card-body py-5 px-md-5">
      <div class="row d-flex justify-content-center">
        <div class="col-lg-8">
          <h2 class="fw-bold mb-3">Змінити дані профілю</h2>
          <div>
            <div class="form-outline mb-3">
              <label class="form-label" for="form3Example3">Введіть новий нікнейм</label>
              <input id="form3Example3" class="form-control" [(ngModel)]="name"
                     type="email"
                     placeholder="{{user.nickName}}">
            </div>

            <div class="form-outline mb-3">
              <label class="form-label" for="form3Example4">Введіть нове призвіще</label>
              <input id="form3Example4" class="form-control" #pass
                     [(ngModel)]="lastName"
                     type="password"
                     placeholder="{{user.lastName}}">
            </div>

            <div class="form-outline mb-3">
              <label class="form-label" for="form3Example5">Введіть нове ім'я</label>
              <input id="form3Example5" class="form-control" #pass
                     [(ngModel)]="firstName"
                     type="text"
                     placeholder="{{user.firstName}}">
            </div>

            <div style="color: red" *ngIf="emptyName">Поле імені не може бути пустим</div>
            <div style="color: red" *ngIf="userExist">Користувач з таким нікнеймом вже існує</div>
            <button (click)="validation()" class="btn btn-primary form-outline w-100 btn-block mb-4">
              Зберегти зміни
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
