@Component({
  selector: 'app-headerc',
  templateUrl: './headerc.component.html',
  styleUrls: ['./headerc.component.css']
})
export class HeadercComponent {

}
<header>
  <div class="head-buttons left-buttons">
    <a href="">Main page</a>
    <a href="">Manga</a>
