import {Component, OnInit} from '@angular/core';
import {environment} from "../../environment";
import {HttpClient} from "@angular/common/http";
import {Router, Routes} from "@angular/router";
import {User} from "../../user";


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css', '../auth-page.component.css', '../../publish/publish.component.css', '../../title-view/title-view.component.css']
})
export class LoginComponent implements OnInit {

  email: string = ''
  password: string = ''
  emptyEmail: boolean = false
  emptyPass: boolean = false

  constructor(private httpclient: HttpClient, private router: Router) {
  }

  ngOnInit(): void {

  }

  submit(): void {
    this.httpclient.post<string>(`${environment.serverUrl}/auth/login`, {
      email: this.email,
      password: this.password,
    }).subscribe(() => {
      this.getUser();
    });
  }

  getUser() {
    if (!this.email) {
      this.emptyEmail = true;
      return;
    }
    if (!this.password) {
      this.emptyPass = true;
      return;
    }

    this.httpclient.get<User>(`${environment.serverUrl}/auth/user`).subscribe(user => {
      localStorage.setItem("user", JSON.stringify(user));
      document.location.reload();
      this.router.navigate(['profile']).then(() => {});
    });
  }
}
<section class="text-center">
  <div class="card mx-4 mx-md-5 shadow-5-strong" style="
        background: hsla(210, 70%, 95%, 0.8); /* светло-синий фон */
        backdrop-filter: blur(30px);
        border: 0;
        ">
    <div class="card-body py-5 px-md-5">

      <div class="row d-flex justify-content-center">
        <div class="col-lg-8">
          <h2 class="fw-bold mb-3" style="color: #0d6efd;">Авторизація</h2> <!-- темно-синий заголовок -->
          <div>
            <div class="form-outline mb-3">
              <input id="form3Example3" class="form-control" [(ngModel)]="email"
                     type="email"
                     placeholder="">
              <label class="form-label" for="form3Example3" style="color: #0d6efd;">Ваш email</label> <!-- темно-синий текст -->
            </div>

            <!-- Password input -->
            <div class="form-outline mb-3">
              <input id="form3Example4" class="form-control" #pass
                     [(ngModel)]="password"
                     type="password"
                     placeholder="">
              <label class="form-label" for="form3Example4" style="color: #0d6efd;">Ваш пароль</label> <!-- темно-синий текст -->
            </div>

            <div style="color: red" *ngIf="emptyEmail">Email field cannot be empty</div>
            <div style="color: red" *ngIf="emptyPass">Password field is empty</div>
            <button (click)="submit()" class="btn btn-primary form-outline w-100 btn-block mb-4" style="background-color: #0d6efd;"> <!-- ярко-синяя кнопка -->
              Ввійти
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
